import React from 'react';

export default function Portfolio(props) {

    const projectStyle = {height:200, width:'100%', border:'2px black', borderRadius:15, justifyContent:'center'}
    const buttonStyle = {backgroundColor:'black', fontSize:30,}
    return(
      <>
       {props.projects.map((project) => {
           return(
               <div style={projectStyle}>
                   <h1 style={{textAlign:'center'}}>{project.title}</h1>
                   <img style={{width:400, height:150, display:'flex', margin:'auto'}}alt={project.title} src={project.img} />
                   <div style={{alignContent:'center'}}><button style={buttonStyle}><a href={project.ghlink}>GitHub Repo</a></button>
                   <button><a href={project.deployed}>Deployed Site</a></button></div>
               </div>
           )
       })}
      </>
    )
}

